function customSonarCommand(properties){

	console.log('---> customSonarCommand ')
	var projectLocation = properties['projectLocation']; 
	var javaBuildCommand = properties['javaBuildCommand'];
	console.log('javaBuildCommand : ' + javaBuildCommand);
	var options = {
            cwd: projectLocation
	 	 };

	 	 exec(javaBuildCommand,options,function(err, stdout, stderr){
     	 	exec(sonar_runner_command,options,function(err, stdout,stderr){
     	 		console.log("--->after executeing sonar-runner");
     	 		console.log('stdout : ' + stdout); 
				utilModule.deleteFolder(projectLocation);
     	 	});
     	 });
}


